  `keyword` varchar(200) DEFAULT NULL COMMENT '关键字',
  `digest` varchar(500) DEFAULT NULL,
  PRIMARY KEY (`id_`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='文章表';

